//**************************************************************************
// Vector-vector add benchmark
//--------------------------------------------------------------------------
//
// This benchmark uses adds to vectors and writes the results to a
// third vector. The input data (and reference data) should be
// generated using the vvadd_gendata.pl perl script and dumped
// to a file named dataset1.h.
 
#include "util.h"

//--------------------------------------------------------------------------
// lsu_failures function

void lsu_failures(int n, int arr[], int arr2[], int arr3[])
{
  for (int i = 0; i < n; i++) {
    double y = arr2[i] * 2.5;
    y = y / 1.3;
    y = y / 1.1;
    y = y / 1.2;
    y = y / 1.3;
    y = y * arr3[i];
    arr[i] = (int) y;
    int x = arr[i] - 1;
    arr[i] = x + 1;
  }
  for (int i = 0; i < n; i++) {
    double y = arr[i] * 2.5;
    y = y / 1.3;
    y = y / 1.1;
    y = y / 1.2;
    y = y / 1.3;
    y = y * arr[i];
    arr2[i] = (int) y;
    int x = arr2[i] - 1;
    arr2[i] = x + 1;
  }
}

